import React, { useState, useEffect, useRef } from 'react';
import { authAPI, tokenManager, userManager } from '../services/api';

const LoginPage = ({ onLogin, onSwitchToSignup }) => {
  const [formData, setFormData] = useState({
    email: '',
    password: ''
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [errorDisplayTime, setErrorDisplayTime] = useState(null);
  const errorTimeoutRef = useRef(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Only clear error if it's been displayed for at least 5 seconds and user is actively typing
    if (error && errorDisplayTime && Date.now() - errorDisplayTime > 5000) {
      setError('');
      setErrorDisplayTime(null);
      if (errorTimeoutRef.current) {
        clearTimeout(errorTimeoutRef.current);
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Don't clear error or form data immediately
    setLoading(true);

    // Clear any existing timeout
    if (errorTimeoutRef.current) {
      clearTimeout(errorTimeoutRef.current);
    }

    try {
      const response = await authAPI.login(formData);

      if (response.success) {
        // Store token and user data
        tokenManager.setToken(response.token);
        userManager.setUser(response.user);

        // Call parent callback
        onLogin(response.user);
      }
    } catch (error) {
      console.error('Login error:', error);
      const errorMessage = error.message || 'Failed to sign in. Please check your credentials.';
      setError(errorMessage);
      setErrorDisplayTime(Date.now());

      // Clear only the password field for security, keep email
      setFormData(prev => ({
        ...prev,
        password: ''
      }));

      // Auto-clear error after 10 seconds
      errorTimeoutRef.current = setTimeout(() => {
        setError('');
        setErrorDisplayTime(null);
      }, 10000);

    } finally {
      setLoading(false);
    }
  };

  // Clear timeout on component unmount
  useEffect(() => {
    return () => {
      if (errorTimeoutRef.current) {
        clearTimeout(errorTimeoutRef.current);
      }
    };
  }, []);

  const handleErrorDismiss = () => {
    setError('');
    setErrorDisplayTime(null);
    if (errorTimeoutRef.current) {
      clearTimeout(errorTimeoutRef.current);
    }
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <div className="login-header">
          <h1>IoPulse</h1>
          <p>DeFi Investment Monitor powered by io.net AI</p>
        </div>

        {error && (
          <div style={{
            background: 'rgba(239, 68, 68, 0.1)',
            border: '1px solid rgba(239, 68, 68, 0.3)',
            borderRadius: '8px',
            padding: '12px',
            marginBottom: '20px',
            color: '#ef4444',
            fontSize: '0.9rem',
            position: 'relative',
            animation: 'fadeIn 0.3s ease-in'
          }}>
            <div style={{ paddingRight: '30px' }}>
              {error}
            </div>
            <button
              onClick={handleErrorDismiss}
              style={{
                position: 'absolute',
                top: '8px',
                right: '8px',
                background: 'none',
                border: 'none',
                color: '#ef4444',
                cursor: 'pointer',
                fontSize: '16px',
                fontWeight: 'bold',
                padding: '4px',
                borderRadius: '4px',
                opacity: 0.7
              }}
              onMouseOver={(e) => e.target.style.opacity = '1'}
              onMouseOut={(e) => e.target.style.opacity = '0.7'}
              title="Dismiss error"
            >
              Ã—
            </button>
          </div>
        )}

        <form className="login-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input
              type="email"
              id="email"
              name="email"
              placeholder="Enter your email"
              value={formData.email}
              onChange={handleInputChange}
              required
              disabled={loading}
            />
          </div>

          <div className="form-group">
            <label htmlFor="password">Password</label>
            <input
              type="password"
              id="password"
              name="password"
              placeholder="Enter your password"
              value={formData.password}
              onChange={handleInputChange}
              required
              disabled={loading}
            />
          </div>

          <button type="submit" className="login-button" disabled={loading}>
            {loading ? 'Signing In...' : 'Sign In'}
          </button>
        </form>

        <div style={{
          textAlign: 'center',
          marginTop: '20px',
          color: '#a0a0a0'
        }}>
          Don't have an account?{' '}
          <button
            type="button"
            onClick={onSwitchToSignup}
            style={{
              background: 'none',
              border: 'none',
              color: '#667eea',
              textDecoration: 'underline',
              cursor: 'pointer',
              fontSize: 'inherit'
            }}
          >
            Sign Up
          </button>
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
