import React, { useState } from "react";
import { recommendationAPI } from "../services/api";

const RecommendationModal = ({
    recommendation,
    onClose,
    onResponse,
}) => {
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const handleSubmit = async (action) => {
        setLoading(true);
        setError("");

        try {
            if (action === "ignore") {
                // Delete the recommendation
                await recommendationAPI.respondToRecommendation(
                    recommendation._id,
                    { action: "ignore" }
                );
                onResponse("ignored");
            } else if (action === "obey") {
                // Update strategy and delete recommendation
                await recommendationAPI.respondToRecommendation(
                    recommendation._id,
                    { action: "obey" }
                );
                onResponse("obeyed");
            }
            onClose();
        } catch (error) {
            setError(error.message || "Failed to respond to recommendation");
        } finally {
            setLoading(false);
        }
    };

    const getActionColor = (action) => {
        switch (action?.toLowerCase()) {
            case "buy":
            case "swap":
                return "text-green-400";
            case "sell":
                return "text-red-400";
            case "hold":
                return "text-yellow-400";
            default:
                return "text-gray-400";
        }
    };

    const getActionIcon = (action) => {
        switch (action?.toLowerCase()) {
            case "buy":
            case "swap":
                return (
                    <svg
                        className="w-5 h-5"
                        fill="currentColor"
                        viewBox="0 0 20 20"
                    >
                        <path
                            fillRule="evenodd"
                            d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-8.707l-3-3a1 1 0 00-1.414 0l-3 3a1 1 0 001.414 1.414L9 9.414V13a1 1 0 102 0V9.414l1.293 1.293a1 1 0 001.414-1.414z"
                            clipRule="evenodd"
                        />
                    </svg>
                );
            case "sell":
                return (
                    <svg
                        className="w-5 h-5"
                        fill="currentColor"
                        viewBox="0 0 20 20"
                    >
                        <path
                            fillRule="evenodd"
                            d="M10 18a8 8 0 100-16 8 8 0 000 16zm1-11a1 1 0 10-2 0v3.586L7.707 9.293a1 1 0 00-1.414 1.414l3 3a1 1 0 001.414 0l3-3a1 1 0 00-1.414-1.414L11 10.586V7z"
                            clipRule="evenodd"
                        />
                    </svg>
                );
            case "hold":
                return (
                    <svg
                        className="w-5 h-5"
                        fill="currentColor"
                        viewBox="0 0 20 20"
                    >
                        <path
                            fillRule="evenodd"
                            d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z"
                            clipRule="evenodd"
                        />
                    </svg>
                );
            default:
                return null;
        }
    };

    const parseRecommendationText = (text) => {
        if (!text) return { action: "HOLD", currentToken: "", newToken: "", period: "" };

        // Parse "Swap ETH for PEPE and hold for 2-4 weeks" or "Don't swap anything and hold ETH for more 1-2 weeks"
        if (text.toLowerCase().includes("don't swap anything")) {
            const holdMatch = text.match(/hold (\w+) for (?:more )?(.+)/i);
            return {
                action: "HOLD",
                currentToken: holdMatch?.[1] || "",
                newToken: holdMatch?.[1] || "",
                period: holdMatch?.[2] || ""
            };
        } else if (text.toLowerCase().includes("swap")) {
            const swapMatch = text.match(/swap (\w+) for (\w+) and hold for (.+)/i);
            return {
                action: "SWAP",
                currentToken: swapMatch?.[1] || "",
                newToken: swapMatch?.[2] || "",
                period: swapMatch?.[3] || ""
            };
        }

        return { action: "HOLD", currentToken: "", newToken: "", period: "" };
    };

    const parsedRec = parseRecommendationText(recommendation.recommendation);

    return (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50">
            <div className="bg-gray-800 rounded-lg p-6 w-full max-w-lg mx-4">
                <div className="flex justify-between items-center mb-4">
                    <h2 className="text-xl font-bold text-white">
                        AI Recommendation
                    </h2>
                    <button
                        onClick={onClose}
                        className="text-gray-400 hover:text-white transition-colors"
                    >
                        <svg
                            className="w-6 h-6"
                            fill="none"
                            stroke="currentColor"
                            viewBox="0 0 24 24"
                        >
                            <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth="2"
                                d="M6 18L18 6M6 6l12 12"
                            />
                        </svg>
                    </button>
                </div>

                <div className="space-y-4 mb-6">
                    {/* Strategy Info */}
                    <div className="p-4 bg-gray-700 rounded-lg">
                        <h3 className="text-white font-semibold mb-2">
                            {recommendation.strategy.title}
                        </h3>
                        <div className="text-sm text-gray-300">
                            Current Holdings: {recommendation.strategy.coin}
                        </div>
                        <div className="text-xs text-gray-400 mt-1">
                            {new Date(recommendation.createdAt).toLocaleString()}
                        </div>
                    </div>

                    {/* Recommendation Details */}
                    <div className="p-4 bg-gray-700 rounded-lg">
                        <div className="flex items-center space-x-2 mb-3">
                            <span
                                className={`flex items-center space-x-2 ${getActionColor(
                                    parsedRec.action
                                )}`}
                            >
                                {getActionIcon(parsedRec.action)}
                                <span className="font-semibold">
                                    {parsedRec.action === "SWAP" ? "SWAP" : "HOLD"}
                                </span>
                            </span>
                            <span className="text-gray-300">•</span>
                            <span className="text-white">
                                Confidence: {recommendation.confidence}%
                            </span>
                        </div>

                        {parsedRec.action === "SWAP" && (
                            <div className="bg-gray-600 rounded p-3 mb-3">
                                <div className="flex items-center justify-between">
                                    <span className="text-gray-300">From:</span>
                                    <span className="text-white font-semibold">{parsedRec.currentToken}</span>
                                </div>
                                <div className="flex items-center justify-center my-2">
                                    <svg className="w-4 h-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                        <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 14l-7 7m0 0l-7-7m7 7V3" />
                                    </svg>
                                </div>
                                <div className="flex items-center justify-between">
                                    <span className="text-gray-300">To:</span>
                                    <span className="text-green-400 font-semibold">{parsedRec.newToken}</span>
                                </div>
                            </div>
                        )}

                        <div className="text-sm text-gray-300 mb-2">
                            <span className="font-medium">Hold Period:</span> {parsedRec.period}
                        </div>

                        <div className="text-sm text-gray-300">
                            <span className="font-medium">Analysis:</span>
                        </div>
                        <p className="text-white text-sm mt-1">
                            {recommendation.explanation}
                        </p>
                    </div>

                    {error && (
                        <div className="p-3 bg-red-500/20 border border-red-500 rounded-lg text-red-200 text-sm">
                            {error}
                        </div>
                    )}
                </div>

                {/* Action Buttons */}
                <div className="flex space-x-3">
                    <button
                        onClick={() => handleSubmit("ignore")}
                        className="flex-1 px-4 py-2 bg-red-600 text-white rounded-lg hover:bg-red-700 transition-colors disabled:opacity-50 flex items-center justify-center space-x-2"
                        disabled={loading}
                    >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M6 18L18 6M6 6l12 12" />
                        </svg>
                        <span>
                            {loading ? "Processing..." : "Ignore"}
                        </span>
                    </button>
                    <button
                        onClick={() => handleSubmit("obey")}
                        className="flex-1 px-4 py-2 bg-green-600 text-white rounded-lg hover:bg-green-700 transition-colors disabled:opacity-50 flex items-center justify-center space-x-2"
                        disabled={loading}
                    >
                        <svg className="w-4 h-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 13l4 4L19 7" />
                        </svg>
                        <span>
                            {loading ? "Processing..." : "I Obey"}
                        </span>
                    </button>
                </div>
            </div>
        </div>
    );
};

export default RecommendationModal;
