import React, { useState } from "react";
import { 
    ArrowLeft, 
    Play, 
    Clock, 
    CheckCircle, 
    AlertCircle, 
    Coins,
    TrendingUp,
    DollarSign,
    Activity
} from "lucide-react";

const StrategyDetailView = ({ strategy, onBack, onRequestRecommendation }) => {
    const [isProcessing, setIsProcessing] = useState(false);
    const [processSteps, setProcessSteps] = useState([
        { 
            id: 1, 
            name: "Market Screening", 
            status: "pending", 
            description: "Analyzing market conditions and trends",
            timestamp: null
        },
        { 
            id: 2, 
            name: "Quantitative Analysis", 
            status: "pending", 
            description: "Running mathematical models and calculations",
            timestamp: null
        },
        { 
            id: 3, 
            name: "Qualitative Due Diligence", 
            status: "pending", 
            description: "Evaluating fundamental factors and news",
            timestamp: null
        },
        { 
            id: 4, 
            name: "Investment Committee Review", 
            status: "pending", 
            description: "Final review and recommendation generation",
            timestamp: null
        }
    ]);

    const handleRequestRecommendation = async () => {
        setIsProcessing(true);

        // Simulate the process steps
        for (let i = 0; i < processSteps.length; i++) {
            setTimeout(() => {
                setProcessSteps(prev => prev.map((step, index) => {
                    if (index === i) {
                        return {
                            ...step,
                            status: "processing",
                            timestamp: new Date().toISOString()
                        };
                    }
                    return step;
                }));

                // Complete the step after a delay
                setTimeout(() => {
                    setProcessSteps(prev => prev.map((step, index) => {
                        if (index === i) {
                            return {
                                ...step,
                                status: "completed"
                            };
                        }
                        return step;
                    }));

                    // If this is the last step, finish processing
                    if (i === processSteps.length - 1) {
                        setTimeout(() => {
                            setIsProcessing(false);
                        }, 500);
                    }
                }, 1500 + (i * 200)); // Varying completion times

            }, i * 2000); // Staggered start times
        }

        // Call the parent callback if provided
        if (onRequestRecommendation) {
            onRequestRecommendation(strategy);
        }
    };

    const getStatusIcon = (status) => {
        switch (status) {
            case "completed":
                return <CheckCircle size={20} className="text-green-400" />;
            case "processing":
                return <Activity size={20} className="text-blue-400 animate-pulse" />;
            case "error":
                return <AlertCircle size={20} className="text-red-400" />;
            default:
                return <Clock size={20} className="text-gray-400" />;
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case "completed":
                return "#10b981";
            case "processing":
                return "#3b82f6";
            case "error":
                return "#ef4444";
            default:
                return "#6b7280";
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return "";
        return new Date(dateString).toLocaleString();
    };

    return (
        <div className="strategy-detail-container">
            {/* Header */}
            <div className="strategy-detail-header">
                <button 
                    onClick={onBack}
                    className="back-button"
                >
                    <ArrowLeft size={20} />
                    Back to Dashboard
                </button>
                <h1 className="strategy-detail-title">{strategy.title}</h1>
            </div>

            {/* Two Column Layout */}
            <div className="strategy-detail-content">
                {/* Left Column - Process Flow (Wide) */}
                <div className="process-flow-column">
                    <div className="process-flow-header">
                        <h2>AI Recommendation Process</h2>
                        <button 
                            onClick={handleRequestRecommendation}
                            disabled={isProcessing}
                            className="request-recommendation-button"
                        >
                            <Play size={16} />
                            {isProcessing ? "Processing..." : "Request Recommendation"}
                        </button>
                    </div>

                    <div className="process-flow-content">
                        {processSteps.map((step, index) => (
                            <div key={step.id} className="process-step">
                                <div className="process-step-indicator">
                                    <div 
                                        className="step-number"
                                        style={{ 
                                            backgroundColor: getStatusColor(step.status),
                                            color: step.status === 'pending' ? '#6b7280' : 'white'
                                        }}
                                    >
                                        {step.status === 'pending' ? step.id : getStatusIcon(step.status)}
                                    </div>
                                    {index < processSteps.length - 1 && (
                                        <div 
                                            className="step-connector"
                                            style={{
                                                backgroundColor: step.status === 'completed' ? '#10b981' : '#374151'
                                            }}
                                        />
                                    )}
                                </div>
                                <div className="process-step-content">
                                    <h3 className="step-title">{step.name}</h3>
                                    <p className="step-description">{step.description}</p>
                                    {step.timestamp && (
                                        <span className="step-timestamp">
                                            {step.status === 'processing' ? 'Started: ' : 'Completed: '}
                                            {formatDate(step.timestamp)}
                                        </span>
                                    )}
                                    {step.status === 'processing' && (
                                        <div className="processing-indicator">
                                            <div className="processing-bar">
                                                <div className="processing-bar-fill"></div>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        ))}
                    </div>

                    {/* Results Area */}
                    <div className="process-results">
                        <h3>Recommendation Results</h3>
                        <div className="results-placeholder">
                            {isProcessing ? (
                                <div className="processing-message">
                                    <Activity size={24} className="animate-pulse" />
                                    <span>AI agents are analyzing your strategy...</span>
                                </div>
                            ) : processSteps.every(step => step.status === 'completed') ? (
                                <div className="results-completed">
                                    <CheckCircle size={24} style={{ color: '#10b981' }} />
                                    <span>Analysis complete! Check your notifications for the recommendation.</span>
                                </div>
                            ) : (
                                <div className="results-waiting">
                                    <Clock size={24} style={{ color: '#6b7280' }} />
                                    <span>Click "Request Recommendation" to start the analysis</span>
                                </div>
                            )}
                        </div>
                    </div>
                </div>

                {/* Right Column - Strategy Info (Narrow) */}
                <div className="strategy-info-column">
                    <div className="strategy-info-card">
                        <h3>Strategy Overview</h3>

                        <div className="strategy-info-details">
                            <div className="info-item">
                                <div className="info-label">
                                    <Coins size={16} />
                                    Asset
                                </div>
                                <div className="info-value">{strategy.coin}</div>
                            </div>

                            <div className="info-item">
                                <div className="info-label">
                                    <DollarSign size={16} />
                                    Amount
                                </div>
                                <div className="info-value">
                                    {strategy.currentAmount} {strategy.coin}
                                </div>
                            </div>

                            <div className="info-item">
                                <div className="info-label">
                                    <TrendingUp size={16} />
                                    Status
                                </div>
                                <div className="info-value">
                                    <span 
                                        className="status-badge"
                                        style={{
                                            backgroundColor: strategy.status === 'active' ? '#10b981' : 
                                                            strategy.status === 'paused' ? '#f59e0b' : '#ef4444',
                                            color: 'white',
                                            padding: '4px 8px',
                                            borderRadius: '12px',
                                            fontSize: '12px',
                                            textTransform: 'capitalize'
                                        }}
                                    >
                                        {strategy.status}
                                    </span>
                                </div>
                            </div>

                            <div className="info-item">
                                <div className="info-label">
                                    <Clock size={16} />
                                    Created
                                </div>
                                <div className="info-value">
                                    {formatDate(strategy.createdAt)}
                                </div>
                            </div>
                        </div>

                        <div className="strategy-description-section">
                            <h4>Description</h4>
                            <p className="strategy-description-text">
                                {strategy.description}
                            </p>
                        </div>

                        {/* Last Recommendation */}
                        {strategy.lastObeyedRecommendation && 
                         strategy.lastObeyedRecommendation.message !== "No recommendations followed yet" && (
                            <div className="last-recommendation">
                                <h4>Last Recommendation</h4>
                                <div className="recommendation-content">
                                    <p>{strategy.lastObeyedRecommendation.message}</p>
                                    <div className="recommendation-meta">
                                        <span 
                                            className="recommendation-type"
                                            style={{
                                                color: strategy.lastObeyedRecommendation.recommendation === 'buy' ? '#10b981' :
                                                       strategy.lastObeyedRecommendation.recommendation === 'sell' ? '#ef4444' : '#f59e0b'
                                            }}
                                        >
                                            {(strategy.lastObeyedRecommendation.recommendation || '').toUpperCase()}
                                        </span>
                                        <span className="recommendation-date">
                                            {formatDate(strategy.lastObeyedRecommendation.timestamp)}
                                        </span>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StrategyDetailView;
