import React, { useState } from "react";
import { X } from "lucide-react";

const CreateStrategyModal = ({ onClose, onSubmit }) => {
    const [formData, setFormData] = useState({
        title: "",
        coin: "",
        description: "",
    });

    // Popular DeFi coins for the dropdown
    const availableCoins = [
        "Bitcoin (BTC)",
        "Ethereum (ETH)",
        "Binance Coin (BNB)",
        "Cardano (ADA)",
        "Solana (SOL)",
        "Polygon (MATIC)",
        "Chainlink (LINK)",
        "Uniswap (UNI)",
        "Avalanche (AVAX)",
        "Polkadot (DOT)",
        "Litecoin (LTC)",
        "Internet Computer (ICP)",
        "Shiba Inu (SHIB)",
        "Wrapped Bitcoin (WBTC)",
        "Dai (DAI)",
        "TRON (TRX)",
        "Cosmos (ATOM)",
        "Ethereum Classic (ETC)",
        "Monero (XMR)",
        "Stellar (XLM)",
    ];

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prev) => ({
            ...prev,
            [name]: value,
        }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        if (formData.title && formData.coin && formData.description) {
            onSubmit(formData);
        }
    };

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                    <h2 className="modal-title">Create New Strategy</h2>
                    <button className="close-button" onClick={onClose}>
                        <X size={20} />
                    </button>
                </div>

                <form className="modal-form" onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="title">Strategy Name</label>
                        <input
                            type="text"
                            id="title"
                            name="title"
                            placeholder="e.g., Conservative Growth Strategy"
                            value={formData.title}
                            onChange={handleInputChange}
                            required
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="coin">Current Investment</label>
                        <select
                            id="coin"
                            name="coin"
                            value={formData.coin}
                            onChange={handleInputChange}
                            required
                        >
                            <option value="">Select a cryptocurrency</option>
                            {availableCoins.map((coin) => (
                                <option key={coin} value={coin}>
                                    {coin}
                                </option>
                            ))}
                        </select>
                    </div>

                    <div className="form-group">
                        <label htmlFor="description">
                            Investment Strategy Description
                        </label>
                        <textarea
                            id="description"
                            name="description"
                            placeholder="Describe your investment goals, risk tolerance, and any specific preferences for the AI to consider..."
                            value={formData.description}
                            onChange={handleInputChange}
                            required
                            rows={4}
                        />
                    </div>

                    <div className="modal-actions">
                        <button
                            type="button"
                            className="cancel-button"
                            onClick={onClose}
                        >
                            Cancel
                        </button>
                        <button type="submit" className="create-button">
                            Create Strategy
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
};

export default CreateStrategyModal;
