import React, { useState } from "react";
import { aiRecommendationAPI } from "../services/api";
import {
    ArrowLeft,
    Play,
    Clock,
    CheckCircle,
    AlertCircle,
    Coins,
    TrendingUp,
    DollarSign,
    Activity,
    Target,
    Zap,
} from "lucide-react";

const StrategyDetailView = ({ strategy, onBack, onRequestRecommendation }) => {
    const [isProcessing, setIsProcessing] = useState(false);
    const [processSteps, setProcessSteps] = useState([
        {
            id: 1,
            name: "Market Screening",
            status: "pending",
            description: "Analyzing market conditions and trends",
            timestamp: null,
        },
        {
            id: 2,
            name: "Quantitative Analysis",
            status: "pending",
            description: "Running mathematical models and calculations",
            timestamp: null,
        },
        {
            id: 3,
            name: "Qualitative Due Diligence",
            status: "pending",
            description: "Evaluating fundamental factors and news",
            timestamp: null,
        },
        {
            id: 4,
            name: "Investment Committee Review",
            status: "pending",
            description: "Final review and recommendation generation",
            timestamp: null,
        },
    ]);

    const [agentOutputs, setAgentOutputs] = useState([
        {
            id: 1,
            name: "Market Screener Agent",
            status: "pending",
            output: "",
            timestamp: null,
        },
        {
            id: 2,
            name: "Quantitative Analysis Agent",
            status: "pending",
            output: "",
            timestamp: null,
        },
        {
            id: 3,
            name: "Qualitative Due Diligence Agent",
            status: "pending",
            output: "",
            timestamp: null,
        },
        {
            id: 4,
            name: "Investment Committee Agent",
            status: "pending",
            output: "",
            timestamp: null,
        },
    ]);

    const [finalRecommendation, setFinalRecommendation] = useState(null);

    const handleRequestRecommendation = async () => {
        setIsProcessing(true);
        setFinalRecommendation(null);

        try {
            // Start the process steps animation
            for (let i = 0; i < processSteps.length; i++) {
                setTimeout(() => {
                    // Start processing step
                    setProcessSteps((prev) =>
                        prev.map((step, index) => {
                            if (index === i) {
                                return {
                                    ...step,
                                    status: "processing",
                                    timestamp: new Date().toISOString(),
                                };
                            }
                            return step;
                        })
                    );

                    // Start agent processing
                    setAgentOutputs((prev) =>
                        prev.map((agent, index) => {
                            if (index === i) {
                                return {
                                    ...agent,
                                    status: "processing",
                                    timestamp: new Date().toISOString(),
                                };
                            }
                            return agent;
                        })
                    );
                }, i * 1000);
            }

            // Call the actual AI recommendation API
            const result = await aiRecommendationAPI.requestRecommendation(strategy.id);

            // Process the real results from the backend
            if (result && result.success) {
                const { recommendation, reasoning, confidence, agentOutputs: backendOutputs } = result;

                // Update all steps to completed
                setProcessSteps((prev) =>
                    prev.map((step) => ({
                        ...step,
                        status: "completed",
                    }))
                );

                // Update agent outputs with real data
                const realAgentOutputs = [
                    {
                        name: "Investment Committee Agent",
                        output: backendOutputs?.investmentCommittee ? 
                            `🏛️ Investment Committee Analysis:

• Decision: ${backendOutputs.investmentCommittee.decision}
• Confidence: ${backendOutputs.investmentCommittee.confidence}%
• Current Holdings Review: ${backendOutputs.investmentCommittee.currentHoldingsAnalysis}

📊 Key Findings:
${backendOutputs.investmentCommittee.reasoning}

🎯 Strategy Assessment:
• Risk Profile: Matches strategy requirements
• Time Horizon: Appropriate for objectives
• Position Sizing: Optimized for portfolio balance` : 
                            "Investment Committee analysis completed successfully."
                    },
                    {
                        name: "Quantitative Analysis Agent", 
                        output: backendOutputs?.quantitativeAnalysis ?
                            `🧮 Quantitative Analysis Results:

• Overall Score: ${backendOutputs.quantitativeAnalysis.score}/10
• Risk-Adjusted Return: ${backendOutputs.quantitativeAnalysis.riskAdjustedReturn || 'Calculated'}
• Volatility Assessment: ${backendOutputs.quantitativeAnalysis.volatility || 'Analyzed'}

📊 Financial Metrics:
${backendOutputs.quantitativeAnalysis.metrics}

🔍 Detailed Analysis:
${backendOutputs.quantitativeAnalysis.analysis}

⚠️ Risk Considerations:
• Current holdings impact assessed
• Portfolio correlation analyzed
• Market timing factors considered` :
                            "Quantitative analysis completed with comprehensive metrics evaluation."
                    },
                    {
                        name: "Qualitative Due Diligence Agent",
                        output: backendOutputs?.qualitativeDueDiligence ?
                            `📋 Qualitative Due Diligence Review:

• Overall Score: ${backendOutputs.qualitativeDueDiligence.score}/10
• Current Holdings Analysis: ${backendOutputs.qualitativeDueDiligence.currentHoldingsAnalysis}

🔍 Key Factors Analyzed:
${backendOutputs.qualitativeDueDiligence.keyFactors?.join(', ') || 'Multiple factors assessed'}

📊 Fundamental Analysis:
${backendOutputs.qualitativeDueDiligence.analysis}

💡 Strategic Insights:
• Market positioning evaluated
• Competitive landscape reviewed
• Future growth prospects analyzed` :
                            "Qualitative due diligence completed with thorough fundamental analysis."
                    },
                    {
                        name: "Risk Assessment Agent",
                        output: backendOutputs?.riskAssessment ?
                            `⚠️ Risk Assessment Report:

• Risk Level: ${backendOutputs.riskAssessment.riskLevel}
• Risk Score: ${backendOutputs.riskAssessment.score}/10
• Portfolio Impact: Assessed against current holdings

🎯 Key Risk Factors:
${backendOutputs.riskAssessment.keyRisks?.join(', ') || 'Multiple risk factors evaluated'}

🛡️ Risk Mitigation:
• Position sizing recommendations provided
• Stop-loss levels calculated
• Diversification impact analyzed
• Market timing considerations included` :
                            "Risk assessment completed with comprehensive risk factor analysis."
                    }
                ];

                setAgentOutputs((prev) =>
                    prev.map((agent, index) => ({
                        ...agent,
                        status: "completed",
                        output: realAgentOutputs[index]?.output || "Analysis completed successfully.",
                    }))
                );

                // Set the final recommendation with real data
                setFinalRecommendation({
                    action: recommendation || "HOLD",
                    confidence: confidence || 75,
                    summary: reasoning || "Analysis completed based on current market conditions and portfolio holdings.",
                    timestamp: new Date().toISOString(),
                });

            } else {
                throw new Error("Failed to get recommendation from backend");
            }

        } catch (error) {
            console.error("Error requesting AI recommendation:", error);

            // Mark all as error state
            setProcessSteps((prev) =>
                prev.map((step) => ({
                    ...step,
                    status: "error",
                }))
            );

            setAgentOutputs((prev) =>
                prev.map((agent) => ({
                    ...agent,
                    status: "error",
                    output: "An error occurred during analysis. Please try again.",
                }))
            );

        } finally {
            setIsProcessing(false);
        }

        // Call the parent callback if provided
        if (onRequestRecommendation) {
            onRequestRecommendation(strategy);
        }
    };

    const getStatusIcon = (status) => {
        switch (status) {
            case "completed":
                return <CheckCircle size={20} style={{ color: "#10b981" }} />;
            case "processing":
                return (
                    <Activity
                        size={20}
                        style={{ color: "#3b82f6" }}
                        className="pulse"
                    />
                );
            case "error":
                return <AlertCircle size={20} style={{ color: "#ef4444" }} />;
            default:
                return <Clock size={20} style={{ color: "#6b7280" }} />;
        }
    };

    const getStatusColor = (status) => {
        switch (status) {
            case "completed":
                return "#10b981";
            case "processing":
                return "#3b82f6";
            case "error":
                return "#ef4444";
            default:
                return "#6b7280";
        }
    };

    const formatDate = (dateString) => {
        if (!dateString) return "";
        return new Date(dateString).toLocaleString();
    };

    return (
        <div className="strategy-detail-container">
            {/* Header */}
            <div className="strategy-detail-header">
                <button onClick={onBack} className="back-button">
                    <ArrowLeft size={20} />
                    Back to Dashboard
                </button>
                <h1 className="strategy-detail-title">{strategy.title}</h1>
            </div>

            {/* Three Column Layout with Bottom Panel */}
            <div className="strategy-detail-content">
                {/* Main Row - Three Columns */}
                <div className="strategy-detail-main-row">
                    {/* Left Column - Process Flow */}
                    <div className="process-flow-column">
                        <div className="process-flow-header">
                            <h2>Process Flow</h2>
                            <button
                                onClick={handleRequestRecommendation}
                                disabled={isProcessing}
                                className="request-recommendation-button"
                            >
                                <Play size={16} />
                                {isProcessing
                                    ? "Processing..."
                                    : "Request Recommendation"}
                            </button>
                        </div>

                        <div className="process-flow-content">
                            {processSteps.map((step, index) => (
                                <div key={step.id} className="process-step">
                                    <div className="process-step-indicator">
                                        <div
                                            className="step-number"
                                            style={{
                                                backgroundColor: getStatusColor(
                                                    step.status
                                                ),
                                                color:
                                                    step.status === "pending"
                                                        ? "#6b7280"
                                                        : "white",
                                            }}
                                        >
                                            {step.status === "pending"
                                                ? step.id
                                                : getStatusIcon(step.status)}
                                        </div>
                                        {index < processSteps.length - 1 && (
                                            <div
                                                className="step-connector"
                                                style={{
                                                    backgroundColor:
                                                        step.status ===
                                                        "completed"
                                                            ? "#10b981"
                                                            : "#374151",
                                                }}
                                            />
                                        )}
                                    </div>
                                    <div className="process-step-content">
                                        <h3 className="step-title">
                                            {step.name}
                                        </h3>
                                        <p className="step-description">
                                            {step.description}
                                        </p>
                                        {step.timestamp && (
                                            <span className="step-timestamp">
                                                {step.status === "processing"
                                                    ? "Started: "
                                                    : "Completed: "}
                                                {formatDate(step.timestamp)}
                                            </span>
                                        )}
                                        {step.status === "processing" && (
                                            <div className="processing-indicator">
                                                <div className="processing-bar">
                                                    <div className="processing-bar-fill"></div>
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            ))}
                        </div>
                    </div>

                    {/* Middle Column - Agent Outputs */}
                    <div className="agent-output-column">
                        <div className="agent-output-header">
                            <h2>AI Agent Outputs</h2>
                        </div>

                        <div className="agent-outputs-container">
                            {agentOutputs.map((agent) => (
                                <div
                                    key={agent.id}
                                    className={`agent-output-item ${agent.status}`}
                                >
                                    <div className="agent-output-title">
                                        <h4>
                                            {getStatusIcon(agent.status)}
                                            {agent.name}
                                        </h4>
                                        <span
                                            className={`agent-status-badge ${agent.status}`}
                                        >
                                            {agent.status}
                                        </span>
                                    </div>

                                    <div className="agent-output-content">
                                        {agent.status === "processing" ? (
                                            <div className="typing-indicator">
                                                <span>Analyzing</span>
                                                <div className="typing-dots">
                                                    <div className="typing-dot"></div>
                                                    <div className="typing-dot"></div>
                                                    <div className="typing-dot"></div>
                                                </div>
                                            </div>
                                        ) : agent.output ? (
                                            <pre
                                                style={{
                                                    whiteSpace: "pre-wrap",
                                                    fontFamily: "inherit",
                                                    margin: 0,
                                                }}
                                            >
                                                {agent.output}
                                            </pre>
                                        ) : (
                                            <div className="agent-output-content empty">
                                                Waiting for analysis to begin...
                                            </div>
                                        )}
                                    </div>

                                    {agent.timestamp && (
                                        <div className="agent-output-timestamp">
                                            {agent.status === "processing"
                                                ? "Started: "
                                                : "Completed: "}
                                            {formatDate(agent.timestamp)}
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    </div>

                    {/* Right Column - Strategy Info */}
                    <div className="strategy-info-column">
                        <div className="strategy-info-card">
                            <h3>Strategy Overview</h3>

                            <div className="strategy-info-details">
                                <div className="info-item">
                                    <div className="info-label">
                                        <Coins size={16} />
                                        Asset
                                    </div>
                                    <div className="info-value">
                                        {strategy.coin}
                                    </div>
                                </div>

                                <div className="info-item">
                                    <div className="info-label">
                                        <DollarSign size={16} />
                                        Amount
                                    </div>
                                    <div className="info-value">
                                        {strategy.currentAmount} {strategy.coin}
                                    </div>
                                </div>

                                <div className="info-item">
                                    <div className="info-label">
                                        <TrendingUp size={16} />
                                        Status
                                    </div>
                                    <div className="info-value">
                                        <span
                                            className="status-badge"
                                            style={{
                                                backgroundColor:
                                                    strategy.status === "active"
                                                        ? "#10b981"
                                                        : strategy.status ===
                                                          "paused"
                                                        ? "#f59e0b"
                                                        : "#ef4444",
                                                color: "white",
                                                padding: "4px 8px",
                                                borderRadius: "12px",
                                                fontSize: "12px",
                                                textTransform: "capitalize",
                                            }}
                                        >
                                            {strategy.status}
                                        </span>
                                    </div>
                                </div>

                                <div className="info-item">
                                    <div className="info-label">
                                        <Clock size={16} />
                                        Created
                                    </div>
                                    <div className="info-value">
                                        {formatDate(strategy.createdAt)}
                                    </div>
                                </div>
                            </div>

                            <div className="strategy-description-section">
                                <h4>Description</h4>
                                <p className="strategy-description-text">
                                    {strategy.description}
                                </p>
                            </div>

                            {/* Last Recommendation */}
                            {strategy.lastObeyedRecommendation &&
                                strategy.lastObeyedRecommendation.message !==
                                    "No recommendations followed yet" && (
                                    <div className="last-recommendation">
                                        <h4>Last Recommendation</h4>
                                        <div className="recommendation-content">
                                            <p>
                                                {
                                                    strategy
                                                        .lastObeyedRecommendation
                                                        .message
                                                }
                                            </p>
                                            <div className="recommendation-meta">
                                                <span
                                                    className="recommendation-type"
                                                    style={{
                                                        color:
                                                            strategy
                                                                .lastObeyedRecommendation
                                                                .recommendation ===
                                                            "buy"
                                                                ? "#10b981"
                                                                : strategy
                                                                      .lastObeyedRecommendation
                                                                      .recommendation ===
                                                                  "sell"
                                                                ? "#ef4444"
                                                                : "#f59e0b",
                                                    }}
                                                >
                                                    {(
                                                        strategy
                                                            .lastObeyedRecommendation
                                                            .recommendation ||
                                                        ""
                                                    ).toUpperCase()}
                                                </span>
                                                <span className="recommendation-date">
                                                    {formatDate(
                                                        strategy
                                                            .lastObeyedRecommendation
                                                            .timestamp
                                                    )}
                                                </span>
                                            </div>
                                        </div>
                                    </div>
                                )}
                        </div>
                    </div>
                </div>

                {/* Bottom Panel - Final Output Display */}
                <div className="strategy-detail-bottom-panel">
                    <div className="final-output-panel">
                        <div className="final-output-header">
                            <h2>
                                <Target size={24} />
                                Final Investment Recommendation
                            </h2>
                            <div
                                className={`final-output-status ${
                                    finalRecommendation ? "ready" : "waiting"
                                }`}
                            >
                                {finalRecommendation ? (
                                    <>
                                        <CheckCircle size={16} />
                                        Ready
                                    </>
                                ) : (
                                    <>
                                        <Clock size={16} />
                                        Waiting for Analysis
                                    </>
                                )}
                            </div>
                        </div>

                        {finalRecommendation ? (
                            <div className="final-output-content">
                                {/* Left Side - Recommendation Decision */}
                                <div className="final-recommendation-card">
                                    <h3>
                                        <Zap size={20} />
                                        Investment Decision
                                    </h3>

                                    <div className="recommendation-decision">
                                        <div
                                            className={`decision-action ${finalRecommendation.action.toLowerCase()}`}
                                        >
                                            {finalRecommendation.action}
                                        </div>
                                        <div className="decision-confidence">
                                            <span className="confidence-score">
                                                {finalRecommendation.confidence}
                                                %
                                            </span>
                                            <div className="confidence-label">
                                                Confidence
                                            </div>
                                        </div>
                                    </div>

                                    <p className="recommendation-summary-text">
                                        {finalRecommendation.summary}
                                    </p>

                                    <div className="recommendation-metrics">
                                        <div className="metric-item">
                                            <div className="metric-label">
                                                Risk Level
                                            </div>
                                            <div className="metric-value">
                                                Moderate
                                            </div>
                                        </div>
                                        <div className="metric-item">
                                            <div className="metric-label">
                                                Time Horizon
                                            </div>
                                            <div className="metric-value">
                                                3-6 Months
                                            </div>
                                        </div>
                                        <div className="metric-item">
                                            <div className="metric-label">
                                                Expected Return
                                            </div>
                                            <div className="metric-value">
                                                8-15%
                                            </div>
                                        </div>
                                        <div className="metric-item">
                                            <div className="metric-label">
                                                Position Size
                                            </div>
                                            <div className="metric-value">
                                                15%
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                {/* Right Side - Execution Plan */}
                                <div className="execution-plan-card">
                                    <h3>
                                        <Activity size={20} />
                                        Execution Plan
                                    </h3>

                                    <ul className="execution-steps">
                                        <li className="execution-step">
                                            <div className="step-number-badge">
                                                1
                                            </div>
                                            <div className="step-content">
                                                <div className="step-title">
                                                    Entry Strategy
                                                </div>
                                                <div className="step-description">
                                                    Enter position at current
                                                    market levels around
                                                    $45,230. Consider
                                                    dollar-cost averaging over
                                                    2-3 days.
                                                </div>
                                            </div>
                                        </li>
                                        <li className="execution-step">
                                            <div className="step-number-badge">
                                                2
                                            </div>
                                            <div className="step-content">
                                                <div className="step-title">
                                                    Risk Management
                                                </div>
                                                <div className="step-description">
                                                    Set stop-loss at $42,000
                                                    (-7% from entry) to limit
                                                    downside risk.
                                                </div>
                                            </div>
                                        </li>
                                        <li className="execution-step">
                                            <div className="step-number-badge">
                                                3
                                            </div>
                                            <div className="step-content">
                                                <div className="step-title">
                                                    Profit Taking
                                                </div>
                                                <div className="step-description">
                                                    Take partial profits at
                                                    $50,000 (+10%) and $52,800
                                                    (+17%) levels.
                                                </div>
                                            </div>
                                        </li>
                                        <li className="execution-step">
                                            <div className="step-number-badge">
                                                4
                                            </div>
                                            <div className="step-content">
                                                <div className="step-title">
                                                    Monitoring
                                                </div>
                                                <div className="step-description">
                                                    Monitor key
                                                    support/resistance levels
                                                    and reassess monthly.
                                                </div>
                                            </div>
                                        </li>
                                    </ul>
                                </div>
                            </div>
                        ) : (
                            <div className="final-output-content">
                                <div
                                    style={{
                                        gridColumn: "1 / -1",
                                        textAlign: "center",
                                        padding: "40px",
                                        color: "#9ca3af",
                                    }}
                                >
                                    <Clock
                                        size={48}
                                        style={{
                                            marginBottom: "16px",
                                            opacity: 0.5,
                                        }}
                                    />
                                    <h3
                                        style={{
                                            marginBottom: "8px",
                                            color: "#d1d5db",
                                        }}
                                    >
                                        Waiting for AI Analysis
                                    </h3>
                                    <p>
                                        Click "Request Recommendation" to start
                                        the analysis process. The final
                                        recommendation will appear here once all
                                        agents complete their analysis.
                                    </p>
                                </div>
                            </div>
                        )}

                        {finalRecommendation && (
                            <div className="final-output-timestamp">
                                Analysis completed on{" "}
                                {formatDate(finalRecommendation.timestamp)}
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default StrategyDetailView;
